

$("#axios").click(function() {
  axios({
    method: 'get',
    url: 'https://brasilapi.com.br/api/cep/v1/14085-079'
  })
  .then(resposta => {
    if (resposta.status === 200) {
      const dados = resposta.data;
        console.log(dados)
      
      const ul = $('<ul></ul>');
      $('body').append(ul);

      const li = $(`<li>O CEP: ${dados.cep} fica na cidade: ${dados.city} no bairro  ${dados.neighborhood} no estado de ${dados.state}</li>`);
      ul.append(li);
    } else{
        throw new Error("Falha na requisição");
        
    }
  })
  .catch(erro => {
    console.error('Erro ao buscar o CEP:', erro);
  })
});

